import React, { useState, useEffect } from 'react';
import './VoiceInput.css';

interface VoiceInputProps {
  onSpeechResult: (text: string) => void;
  disabled?: boolean;
}

const VoiceInput: React.FC<VoiceInputProps> = ({ onSpeechResult, disabled = false }) => {
  const [isListening, setIsListening] = useState(false);
  const [speechRecognition, setSpeechRecognition] = useState<SpeechRecognition | null>(null);
  const [animationLevel, setAnimationLevel] = useState(0);
  const [voiceAmplitude, setVoiceAmplitude] = useState<number[]>([0, 0, 0, 0, 0]);
  const [errorMessage, setErrorMessage] = useState<string | null>(null);
  const audioContextRef = useRef<AudioContext | null>(null);
  const analyserRef = useRef<AnalyserNode | null>(null);
  const mediaStreamRef = useRef<MediaStream | null>(null);

  // Animation effect
  useEffect(() => {
    let animationInterval: NodeJS.Timeout | null = null;

    if (isListening) {
      animationInterval = setInterval(() => {
        setAnimationLevel(prev => (prev + 1) % 4); // Cycle through 0-3
      }, 300);
    } else if (animationInterval) {
      clearInterval(animationInterval);
      setAnimationLevel(0);
    }

    return () => {
      if (animationInterval) clearInterval(animationInterval);
    };
  }, [isListening]);

  // Initialize speech recognition
  useEffect(() => {
    if (typeof window !== 'undefined') {
      // Check if browser supports speech recognition
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

      if (SpeechRecognition) {
        const recognition = new SpeechRecognition();
        recognition.continuous = false;
        recognition.interimResults = false;
        recognition.lang = 'en-US';

        recognition.onresult = (event) => {
          const transcript = event.results[0][0].transcript;
          onSpeechResult(transcript);
          setIsListening(false);
        };

        recognition.onerror = (event) => {
          console.error('Speech recognition error', event.error);
          setErrorMessage(`Error: ${event.error}`);
          setIsListening(false);
        };

        recognition.onend = () => {
          setIsListening(false);
        };

        setSpeechRecognition(recognition);
      } else {
        setErrorMessage('Speech recognition not supported in this browser');
      }
    }
  }, [onSpeechResult]);

  const toggleListening = () => {
    if (disabled || !speechRecognition) return;

    if (isListening) {
      speechRecognition.stop();
      setIsListening(false);
    } else {
      setErrorMessage(null);
      speechRecognition.start();
      setIsListening(true);
    }
  };

  return (
    <div className="voice-input-container">
      <button
        className={`voice-input-button ${isListening ? 'listening' : ''} ${disabled ? 'disabled' : ''}`}
        onClick={toggleListening}
        disabled={disabled || !speechRecognition}
        title={errorMessage || (isListening ? 'Tap to stop' : 'Tap to speak')}
        aria-label="Voice input"
      >
        <svg
          className="microphone-svg"
          width="24"
          height="24"
          viewBox="0 0 24 24"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          {/* Microphone body */}
          <rect
            x="9"
            y="2"
            width="6"
            height="12"
            rx="3"
            fill={isListening ? "#FFFFFF" : "#555555"}
          />
          {/* Microphone base */}
          <path
            d="M5 10V11C5 14.866 8.13401 18 12 18V18C15.866 18 19 14.866 19 11V10"
            stroke={isListening ? "#FFFFFF" : "#555555"}
            strokeWidth="2"
            strokeLinecap="round"
          />
          {/* Microphone stand */}
          <line
            x1="12"
            y1="18"
            x2="12"
            y2="22"
            stroke={isListening ? "#FFFFFF" : "#555555"}
            strokeWidth="2"
            strokeLinecap="round"
          />
          {/* Microphone base */}
          <line
            x1="8"
            y1="22"
            x2="16"
            y2="22"
            stroke={isListening ? "#FFFFFF" : "#555555"}
            strokeWidth="2"
            strokeLinecap="round"
          />
        </svg>
        {isListening && (
          <div className="sound-waves">
            <div className={`wave wave-1 ${animationLevel >= 1 ? 'active' : ''}`}></div>
            <div className={`wave wave-2 ${animationLevel >= 2 ? 'active' : ''}`}></div>
            <div className={`wave wave-3 ${animationLevel >= 3 ? 'active' : ''}`}></div>
          </div>
        )}
      </button>
      {errorMessage && <div className="voice-input-error">{errorMessage}</div>}
    </div>
  );
};

export default VoiceInput;
