import React, { useState } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Pie, Bar, Line } from 'react-chartjs-2';
import './DataVisualization.css';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

interface DataVisualizationProps {
  data: {
    columns: string[];
    rows: any[];
  };
}

const DataVisualization: React.FC<DataVisualizationProps> = ({ data }) => {
  const [chartType, setChartType] = useState<'pie' | 'bar' | 'line' | null>(null);
  const [labelColumn, setLabelColumn] = useState<string | null>(null);
  const [valueColumn, setValueColumn] = useState<string | null>(null);
  const [isFormatted, setIsFormatted] = useState<boolean>(false);

  // Find numeric columns for values
  const numericColumns = data.columns.filter(col => {
    if (data.rows.length === 0) return false;
    const value = data.rows[0][col];
    return typeof value === 'number' || !isNaN(Number(value));
  });

  // Find string/categorical columns for labels
  const categoricalColumns = data.columns.filter(col => {
    if (data.rows.length === 0) return false;
    const value = data.rows[0][col];
    return typeof value === 'string' || value instanceof String;
  });

  const handleVisualize = (type: 'pie' | 'bar' | 'line') => {
    setChartType(type);

    // Auto-select columns if not already selected
    if (!labelColumn && categoricalColumns.length > 0) {
      setLabelColumn(categoricalColumns[0]);
    }

    if (!valueColumn && numericColumns.length > 0) {
      setValueColumn(numericColumns[0]);
    }
  };

  const formatValue = (value: number): string => {
    if (isFormatted) {
      // Check if it's likely a currency value
      if (valueColumn?.toLowerCase().includes('salary') ||
          valueColumn?.toLowerCase().includes('price') ||
          valueColumn?.toLowerCase().includes('cost') ||
          valueColumn?.toLowerCase().includes('revenue')) {
        return `$${value.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;
      }

      // Format large numbers with commas
      if (value >= 1000) {
        return value.toLocaleString();
      }

      // Format percentages
      if (valueColumn?.toLowerCase().includes('percent') || valueColumn?.toLowerCase().includes('rate')) {
        return `${value.toFixed(2)}%`;
      }
    }

    return String(value);
  };

  const prepareChartData = () => {
    if (!labelColumn || !valueColumn || !data.rows.length) {
      return {
        labels: [],
        datasets: [{
          data: [],
          backgroundColor: [],
        }]
      };
    }

    // Group data by label column and sum values
    const groupedData: Record<string, number> = {};

    data.rows.forEach(row => {
      const label = String(row[labelColumn]);
      const value = Number(row[valueColumn]);

      if (!isNaN(value)) {
        if (groupedData[label]) {
          groupedData[label] += value;
        } else {
          groupedData[label] = value;
        }
      }
    });

    // Generate colors
    const generateColors = (count: number) => {
      const colors = [];
      for (let i = 0; i < count; i++) {
        const hue = (i * 137) % 360; // Use golden angle for nice distribution
        colors.push(`hsl(${hue}, 70%, 60%)`);
      }
      return colors;
    };

    const labels = Object.keys(groupedData);
    const values = Object.values(groupedData);
    const colors = generateColors(labels.length);

    return {
      labels,
      datasets: [{
        label: valueColumn,
        data: values,
        backgroundColor: colors,
        borderColor: colors.map(color => color.replace('60%', '50%')),
        borderWidth: 1,
      }]
    };
  };

  const chartData = prepareChartData();

  const renderChart = () => {
    if (!chartType || !labelColumn || !valueColumn) {
      return null;
    }

    const options = {
      responsive: true,
      plugins: {
        legend: {
          position: 'top' as const,
        },
        title: {
          display: true,
          text: `${valueColumn} by ${labelColumn}`,
        },
      },
    };

    switch (chartType) {
      case 'pie':
        return <Pie data={chartData} options={options} />;
      case 'bar':
        return <Bar data={chartData} options={options} />;
      case 'line':
        return <Line data={chartData} options={options} />;
      default:
        return null;
    }
  };

  return (
    <div className="data-visualization">
      <div className="visualization-controls" style={{ marginBottom: '1rem' }}>
        <div style={{ marginBottom: '0.5rem' }}>
          <button
            onClick={() => handleVisualize('pie')}
            style={{
              backgroundColor: chartType === 'pie' ? 'var(--accent-color)' : '#f0f0f0',
              color: chartType === 'pie' ? 'white' : 'var(--text-primary)',
              marginRight: '0.5rem'
            }}
          >
            Pie Chart
          </button>
          <button
            onClick={() => handleVisualize('bar')}
            style={{
              backgroundColor: chartType === 'bar' ? 'var(--accent-color)' : '#f0f0f0',
              color: chartType === 'bar' ? 'white' : 'var(--text-primary)',
              marginRight: '0.5rem'
            }}
          >
            Bar Chart
          </button>
          <button
            onClick={() => handleVisualize('line')}
            style={{
              backgroundColor: chartType === 'line' ? 'var(--accent-color)' : '#f0f0f0',
              color: chartType === 'line' ? 'white' : 'var(--text-primary)',
            }}
          >
            Line Chart
          </button>
        </div>

        {chartType && (
          <div style={{ display: 'flex', gap: '1rem' }}>
            <div>
              <label style={{ display: 'block', marginBottom: '0.25rem' }}>Label Column:</label>
              <select
                value={labelColumn || ''}
                onChange={(e) => setLabelColumn(e.target.value)}
                style={{ padding: '0.25rem' }}
              >
                <option value="">Select column</option>
                {categoricalColumns.map(col => (
                  <option key={col} value={col}>{col}</option>
                ))}
              </select>
            </div>

            <div>
              <label style={{ display: 'block', marginBottom: '0.25rem' }}>Value Column:</label>
              <select
                value={valueColumn || ''}
                onChange={(e) => setValueColumn(e.target.value)}
                style={{ padding: '0.25rem' }}
              >
                <option value="">Select column</option>
                {numericColumns.map(col => (
                  <option key={col} value={col}>{col}</option>
                ))}
              </select>
            </div>
          </div>
        )}
      </div>

      <div className="chart-container" style={{ height: '400px' }}>
        {renderChart()}
      </div>
    </div>
  );
};

export default DataVisualization;
