import React, { useState } from 'react';
import './App.css';
import ChatInterface from './components/ChatInterface';
import EmployeeData from './components/EmployeeData';

function App() {
  const [currentView, setCurrentView] = useState<'chat' | 'employees'>('employees');

  return (
    <div className="App">
      <header className="card" style={{
        padding: '1.25rem',
        margin: '1.5rem 1rem',
        backgroundColor: 'white'
      }}>
        <h1 style={{
          margin: 0,
          fontSize: '24px',
          color: 'var(--text-primary)',
          textAlign: 'center'
        }}>
          {currentView === 'chat' ? 'SQL Query Generator' : 'Employee Data'}
        </h1>
        <div style={{
          display: 'flex',
          justifyContent: 'center',
          marginTop: '1rem',
          gap: '1rem'
        }}>
          <button
            onClick={() => setCurrentView('chat')}
            style={{
              backgroundColor: currentView === 'chat' ? 'var(--accent-color)' : '#f0f0f0',
              color: currentView === 'chat' ? 'white' : 'var(--text-primary)',
            }}
          >
            SQL Query Tool
          </button>
          <button
            onClick={() => setCurrentView('employees')}
            style={{
              backgroundColor: currentView === 'employees' ? 'var(--accent-color)' : '#f0f0f0',
              color: currentView === 'employees' ? 'white' : 'var(--text-primary)',
            }}
          >
            Employee Directory
          </button>
        </div>
      </header>
      <main style={{ padding: '0 1rem' }}>
        {currentView === 'chat' ? <ChatInterface /> : <EmployeeData />}
      </main>
    </div>
  );
}

export default App;
