import React, { useState } from 'react';
import axios from 'axios';
import ThinkingAnimation from './ThinkingAnimation';

interface Message {
  text: string;
  isUser: boolean;
}

const ChatInterface: React.FC = () => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputText, setInputText] = useState('');
  const [isThinking, setIsThinking] = useState(false);

  const handleSend = async () => {
    if (!inputText.trim()) return;

    const userMessage: Message = {
      text: inputText,
      isUser: true,
    };

    setMessages(prev => [...prev, userMessage]);
    setInputText('');
    setIsThinking(true);

    try {
      const response = await axios.post('http://localhost:8000/api/chat', {
        message: inputText,
      });

      const botMessage: Message = {
        text: response.data.response,
        isUser: false,
      };

      setMessages(prev => [...prev, botMessage]);
      setIsThinking(false);
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  return (
    <div className="chat-container" style={{ maxWidth: '800px', margin: '0 auto', padding: '20px' }}>
      <div className="messages" style={{ 
        height: '70vh', 
        overflowY: 'auto',
        border: '1px solid #ccc',
        borderRadius: '5px',
        padding: '20px',
        marginBottom: '20px'
      }}>        {messages.map((message, index) => (
          <div
            key={index}
            style={{
              display: 'flex',
              justifyContent: message.isUser ? 'flex-end' : 'flex-start',
              marginBottom: '10px'
            }}
          >            <div style={{
              backgroundColor: message.isUser ? '#007bff' : '#e9ecef',
              color: message.isUser ? 'white' : 'black',
              padding: '15px',
              borderRadius: '15px',
              maxWidth: '80%',
              whiteSpace: 'pre-wrap',
              fontFamily: message.isUser ? 'inherit' : 'system-ui, -apple-system, sans-serif',
            }}>
              {message.isUser ? (
                message.text
              ) : (
                message.text.split('\n').map((line, i) => {
                  if (line.startsWith('```sql')) {
                    return null; // Skip the SQL marker
                  } else if (line === '```') {
                    return null; // Skip code block end marker
                  } else if (line.startsWith('üîç') || line.startsWith('üìù') || line.startsWith('üìå')) {
                    return (
                      <div key={i} style={{ 
                        fontWeight: 'bold', 
                        marginTop: i > 0 ? '15px' : 0,
                        marginBottom: '8px',
                        color: '#2c3e50'
                      }}>
                        {line}
                      </div>
                    );
                  } else if (message.text.includes('```sql')) {
                    // If we're inside a code block
                    const prevLine = message.text.split('\n')[i - 1] || '';
                    if (prevLine.includes('```sql')) {
                      return (
                        <div key={i} style={{
                          backgroundColor: '#1e1e1e',
                          padding: '12px',
                          borderRadius: '8px',
                          color: '#e6e6e6',
                          fontFamily: 'Consolas, monospace',
                          marginTop: '8px',
                          marginBottom: '8px',
                          overflowX: 'auto'
                        }}>
                          {line}
                        </div>
                      );
                    }
                  }
                  return <div key={i} style={{ marginBottom: '4px' }}>{line}</div>;
                })
              )}
            </div>
          </div>
        ))}
        {isThinking && (
          <div style={{ display: 'flex', justifyContent: 'flex-start', marginBottom: '10px' }}>
            <ThinkingAnimation />
          </div>
        )}
      </div>
      <div style={{ display: 'flex', gap: '10px' }}>
        <input
          type="text"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
          placeholder="Type your message..."
          style={{
            flex: 1,
            padding: '10px',
            borderRadius: '5px',
            border: '1px solid #ccc'
          }}
        />
        <button
          onClick={handleSend}
          style={{
            padding: '10px 20px',
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatInterface;
