import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from 'axios';
import ThinkingAnimation from './ThinkingAnimation';
import { CodeBlock } from './CodeBlock';
import QueryResultTable from './QueryResultTable';
import './ChatInterface.css';

interface Message {
  text: string;
  isUser: boolean;
}

const ChatInterface: React.FC = () => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputText, setInputText] = useState('');
  const [isThinking, setIsThinking] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLTextAreaElement>(null);

  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages, scrollToBottom]);

  const renderMessage = (message: Message) => {
    if (message.isUser) {
      return <div className="message user-message">{message.text}</div>;
    }

    // Check if the message contains a table (starts with "ðŸ“Š Found")
    if (message.text.includes("ðŸ“Š Found")) {
      return (
        <div className="message bot-message">
          <QueryResultTable markdown={message.text} />
        </div>
      );
    }

    return (
      <div className="message bot-message">
        <CodeBlock text={message.text} />
      </div>
    );
  };

  const handleSend = useCallback(async () => {
    if (!inputText.trim()) return;

    const userMessage: Message = {
      text: inputText,
      isUser: true,
    };

    setMessages(prev => [...prev, userMessage]);
    setInputText('');
    setIsThinking(true);

    try {
      const response = await axios.post('http://localhost:8000/api/chat', {
        message: inputText,
      });

      const botMessage: Message = {
        text: response.data.response,
        isUser: false,
      };

      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage: Message = {
        text: 'Sorry, I encountered an error processing your request.',
        isUser: false,
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsThinking(false);
    }
  }, [inputText]);

  const handleKeyPress = (event: React.KeyboardEvent) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      handleSend();
    }
  };

  return (
    <div className="chat-container">
      <div className="messages-container">
        {messages.map((msg, index) => (
          <div key={index} className={`message-wrapper ${msg.isUser ? 'user' : 'bot'}`}>
            {renderMessage(msg)}
          </div>
        ))}
        {isThinking && (
          <div className="message-wrapper bot">
            <div className="message bot-message">
              <ThinkingAnimation />
            </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>
      <div className="input-container">
        <textarea
          ref={inputRef}
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Ask a question..."
          rows={1}
        />
        <button onClick={handleSend} disabled={!inputText.trim() || isThinking}>
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatInterface;
