import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from 'axios';
import ThinkingAnimation from './ThinkingAnimation';
import { CodeBlock } from './CodeBlock';
import QueryResultTable from './QueryResultTable';
import DataVisualization from './DataVisualization';

interface Message {
  text: string;
  isUser: boolean;
  data?: any;
}

const ChatInterface: React.FC = () => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputText, setInputText] = useState('');
  const [isThinking, setIsThinking] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLTextAreaElement>(null);

  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages, scrollToBottom]);

  const handleSend = useCallback(async () => {
    if (!inputText.trim()) return;

    const userMessage: Message = {
      text: inputText,
      isUser: true,
    };

    setMessages(prev => [...prev, userMessage]);
    setInputText('');
    setIsThinking(true);

    try {
      const response = await axios.post('http://localhost:8000/api/chat', {
        message: inputText,
      });

      // The response now contains structured data
      const responseData = response.data;

      // Create a formatted message for display
      let displayText = '';

      // Add SQL query if available
      if (responseData.sql_query) {
        displayText += `SQL Query:\n\`\`\`sql\n${responseData.sql_query}\n\`\`\`\n\n`;
      }

      // Add explanation if available
      if (responseData.explanation && responseData.explanation.trim()) {
        displayText += `${responseData.explanation}\n\n`;
      }

      // Add success/error message
      if (!responseData.success && responseData.error) {
        displayText += `Error: ${responseData.error}\n\n`;
      } else if (responseData.message) {
        // For data modification queries (UPDATE, INSERT, DELETE)
        if (responseData.query_type && ['UPDATE', 'INSERT', 'DELETE'].includes(responseData.query_type)) {
          displayText += `✅ ${responseData.message}\n\n`;
        } else {
          // For SELECT queries
          displayText += `${responseData.message}\n\n`;
        }
      }

      const botMessage: Message = {
        text: displayText,
        isUser: false,
        data: responseData.data // Store the structured data for rendering
      };

      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      console.error('Error sending message:', error);
      setMessages(prev => [...prev, {
        text: "Sorry, I encountered an error while generating the response. Please try again.",
        isUser: false
      }]);
    } finally {
      setIsThinking(false);
    }
  }, [inputText]);

  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      // Ctrl/Cmd + Enter to send message
      if ((e.ctrlKey || e.metaKey) && e.key === 'Enter') {
        handleSend();
      }
      // Esc to clear input
      if (e.key === 'Escape') {
        setInputText('');
      }
      // Ctrl/Cmd + / to focus input
      if ((e.ctrlKey || e.metaKey) && e.key === '/') {
        e.preventDefault();
        inputRef.current?.focus();
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [handleSend, setInputText]);

  const toggleVisualization = (index: number) => {
    setMessages(prev =>
      prev.map((msg, i) =>
        i === index ? { ...msg, showVisualization: !msg.showVisualization } : msg
      )
    );
  };

  const renderMessage = (message: Message, index: number) => {
    const text = message.text;
    const sqlMatches = text.match(/```sql\n([\s\S]*?)```/g);

    if (message.isUser) {
      return (
        <div className="card" style={{
          backgroundColor: 'var(--accent-color)',
          color: 'white',
          padding: '1rem 1.25rem',
          maxWidth: '80%',
          marginBottom: '1rem'
        }}>
          {text}
        </div>
      );
    }

    // Check if data is suitable for visualization (has numeric columns)
    const hasVisualizableData = message.data &&
                               message.data.columns &&
                               message.data.rows &&
                               message.data.rows.length > 0 &&
                               message.data.columns.some((col: string) => {
                                 const value = message.data.rows[0][col];
                                 return typeof value === 'number' || !isNaN(Number(value));
                               });

    // Render the message with SQL code blocks and data table
    return (
      <div style={{ maxWidth: '90%', marginBottom: '1rem' }}>
        {/* Render SQL and text parts */}
        {text && (
          <div className="card" style={{
            backgroundColor: 'white',
            color: 'var(--text-primary)',
            padding: '1rem 1.25rem',
            marginBottom: '0.5rem'
          }}>
            {sqlMatches ? (
              // If there are SQL code blocks, split and render them
              text.split(/(```sql\n[\s\S]*?```)/g).map((part, partIndex) => {
                if (part.startsWith('```sql')) {
                  const code = part.replace(/```sql\n([\s\S]*?)```/, '$1').trim();
                  return <CodeBlock key={partIndex} code={code} />;
                }
                return part ? <p key={partIndex}>{part}</p> : null;
              })
            ) : (
              // Otherwise just render the text
              text
            )}
          </div>
        )}

        {/* Render data table if available */}
        {message.data && message.data.columns && message.data.rows && message.data.columns.length > 0 && (
          <div style={{ marginTop: '1rem' }}>
            <QueryResultTable
              headers={message.data.columns.map((col: string) => {
                // Format column headers for better readability
                if (col === 'department_identifier' && message.data.columns.includes('department_name')) {
                  return 'Department ID';
                } else if (col === 'department_name') {
                  return 'Department';
                } else if (col === 'employee_identifier') {
                  return 'Employee ID';
                } else if (col === 'employee_first_name') {
                  return 'First Name';
                } else if (col === 'employee_last_name') {
                  return 'Last Name';
                } else if (col === 'employee_salary') {
                  return 'Salary';
                } else if (col === 'employee_hire_date') {
                  return 'Hire Date';
                }
                return col;
              })}
              data={message.data.rows.map((row: any) =>
                message.data.columns.map((col: string) => {
                  // Format specific columns for better display
                  if (col === 'employee_salary' && typeof row[col] === 'number') {
                    return `$${row[col].toLocaleString()}`;
                  } else if (col === 'employee_hire_date') {
                    return new Date(row[col]).toLocaleDateString();
                  }
                  return row[col];
                })
              )}
            />

            {/* Visualization options directly at the top of the table */}
            {hasVisualizableData && (
              <div style={{ marginTop: '1rem' }}>
                <DataVisualization data={message.data} />
              </div>
            )}
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="card" style={{
      maxWidth: '900px',
      margin: '0 auto',
      padding: '1.5rem',
      height: '80vh',
      display: 'flex',
      flexDirection: 'column',
      backgroundColor: 'white'
    }}>
      <div className="card" style={{
        flex: 1,
        overflowY: 'auto',
        padding: '1.25rem',
        marginBottom: '1.25rem',
        border: '1px solid var(--border-color)',
        backgroundColor: '#f9fafb'
      }}>
        {messages.map((message, index) => (
          <div
            key={index}
            style={{
              display: 'flex',
              justifyContent: message.isUser ? 'flex-end' : 'flex-start',
            }}
          >
            {renderMessage(message, index)}
          </div>
        ))}
        {isThinking && (
          <div style={{ display: 'flex', justifyContent: 'flex-start' }}>
            <ThinkingAnimation />
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      <div className="card" style={{
        display: 'flex',
        gap: '1rem',
        padding: '1rem',
        backgroundColor: 'white',
        borderTop: '1px solid var(--border-color)'
      }}>
        <textarea
          ref={inputRef}
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === 'Enter' && !e.shiftKey) {
              e.preventDefault();
              handleSend();
            }
          }}
          placeholder="Type your question here... (Ctrl + / to focus, Ctrl + Enter to send)"
          style={{
            flex: 1,
            resize: 'none',
            minHeight: '2.5rem',
            maxHeight: '150px',
            padding: '0.75rem 1rem',
            backgroundColor: 'white'
          }}
        />
        <button
          onClick={() => handleSend()}
          disabled={isThinking || !inputText.trim()}
          style={{
            opacity: isThinking || !inputText.trim() ? 0.6 : 1
          }}
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatInterface;
