import React from 'react';

const ThinkingAnimation: React.FC = () => {
  return (
    <div className="thinking-animation" style={{
      display: 'flex',
      alignItems: 'center',
      gap: '4px',
      padding: '10px 15px',
      backgroundColor: '#e9ecef',
      borderRadius: '15px',
      maxWidth: '70%',
      animation: 'fadeIn 0.3s ease-in'
    }}>
      <div style={{
        width: '8px',
        height: '8px',
        backgroundColor: '#6c757d',
        borderRadius: '50%',
        animation: 'bounce 0.8s infinite'
      }} />
      <div style={{
        width: '8px',
        height: '8px',
        backgroundColor: '#6c757d',
        borderRadius: '50%',
        animation: 'bounce 0.8s infinite 0.2s'
      }} />
      <div style={{
        width: '8px',
        height: '8px',
        backgroundColor: '#6c757d',
        borderRadius: '50%',
        animation: 'bounce 0.8s infinite 0.4s'
      }} />
      <style>{`
        @keyframes bounce {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-10px); }
        }
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }
      `}</style>
    </div>
  );
};

export default ThinkingAnimation;
