import React, { useState } from 'react';
import './App.css';
import ChatInterface from './components/ChatInterface';
import EmployeePage from './components/EmployeePage';

function App() {
  const [currentPage, setCurrentPage] = useState<'chat' | 'employees'>('employees');

  return (
    <div className="App">
      <nav className="card" style={{
        padding: '0.75rem 1.25rem',
        margin: '1rem 1rem 0',
        backgroundColor: 'white',
        display: 'flex',
        justifyContent: 'center',
        gap: '1rem'
      }}>
        <button
          onClick={() => setCurrentPage('chat')}
          style={{
            backgroundColor: currentPage === 'chat' ? 'var(--accent-color)' : '#f0f0f0',
            color: currentPage === 'chat' ? 'white' : 'var(--text-primary)',
          }}
        >
          SQL Query Generator
        </button>
        <button
          onClick={() => setCurrentPage('employees')}
          style={{
            backgroundColor: currentPage === 'employees' ? 'var(--accent-color)' : '#f0f0f0',
            color: currentPage === 'employees' ? 'white' : 'var(--text-primary)',
          }}
        >
          Employee Directory
        </button>
      </nav>

      {currentPage === 'chat' ? (
        <>
          <header className="card" style={{
            padding: '1.25rem',
            margin: '1rem 1rem',
            backgroundColor: 'white'
          }}>
            <h1 style={{
              margin: 0,
              fontSize: '24px',
              color: 'var(--text-primary)',
              textAlign: 'center'
            }}>SQL Query Generator</h1>
          </header>
          <main style={{ padding: '0 1rem' }}>
            <ChatInterface />
          </main>
        </>
      ) : (
        <EmployeePage />
      )}
    </div>
  );
}

export default App;
